import React, { useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

function Mapa() {

  useEffect(() => {
    // Initialize the UI.
    let email = "mcovarrubias@uc.cl";
    let password = "18640729";
    const payload = {
        "type": "JOIN",
        "payload": {
            "authorization": `Basic ${btoa(`${email}:${password}`)}`
        }
    }
    var restaurants = {}
    var destinations = {}

    const socket = new WebSocket("wss://tarea2-2023-1-dev-z2fqxmm2ja-uc.a.run.app/connect"); // replace with your server's URL
    socket.onopen = () => {
      console.log('WebSocket connection established.');
      socket.send(JSON.stringify(payload));
      socket.addEventListener('message', (event) => {
        const data = JSON.parse(event.data);
        console.log(data);

      });
       if (data.type == 'RESTAURANTS') {
            // recorremos los restaurantes y agregamos un marker para cada uno:
            restaurants = data.payload;
        } else if (data.type == 'DESTINATIONS') {
            // lo mismo para los destinos
            destinations = data.payload;
        }
    };



  }, []); // empty dependency array ensures this effect only runs once

  return (
    <MapContainer center={[-33.35, -70.5]} zoom={13} scrollWheelZoom={false} style={{ height: '400px' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <Marker position={[-33.35, -70.5]}>
        <Popup>
          A pretty CSS3 popup. <br /> Easily customizable.
        </Popup>
      </Marker>
    </MapContainer>
  );
}

export default Mapa;
