import React, { useEffect, useState } from 'react';

function App() {
  const [authenticated, setAuthenticated] = useState(false);
  const [token, setToken] = useState('');

  const email = 'mcovarrubias@uc.cl';
  const password = '18640729';

  const handleLogin = () => {
    const socket = new WebSocket('ws://localhost:8080'); // replace with your server's URL
    payload = {
        "type": "JOIN",
        "payload": {
            "authorization": `Basic ${btoa(`${email}:${password}`)}`
        }
    }
    socket.onopen = () => {
      console.log('WebSocket connection established.');
      socket.send(JSON.stringify({ type: 'login', email, password }));
    };
    socket.onmessage = (event) => {
      const message = JSON.parse(event.data);
      switch (message.type) {
        case 'authenticated':
          setAuthenticated(true);
          setToken(message.token);
          console.log('WebSocket connection authenticated.');
          break;
        case 'unauthenticated':
          setAuthenticated(false);
          setToken('');
          console.log('WebSocket connection authentication failed.');
          break;
        // handle other message types
        default:
          console.log(`Received message: ${event.data}`);
      }
    };
    socket.onclose = (event) => {
      console.log(`WebSocket connection closed with code ${event.code}.`);
    };
  };

  const handleLogout = () => {
    setAuthenticated(false);
    setToken('');
  };

  if (!authenticated) {
    return (
      <div>
        <h1>Login</h1>
        <form onSubmit={(e) => e.preventDefault()}>
          <label>
            Email:
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          </label>
          <br />
          <label>
            Password:
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </label>
          <br />
          <button onClick={handleLogin}>Login</button>
        </form>
      </div>
    );
  }

  return (
    <div className="App">
      <h1>Authenticated WebSocket Example</h1>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default App;
