import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

let email = "mcovarrubias@uc.cl";
let password = "18640729";
const payload = {
    "type": "JOIN",
    "payload": {
        "authorization": `Basic ${btoa(`${email}:${password}`)}`
    }
}

function Mapa() {

  const [restaurants, setRestaurants] = useState([]);
  const [destinations, setDestinations] = useState([]);

  useEffect(() => {
    // Initialize the UI.

    const socket = new WebSocket("wss://tarea2-2023-1-dev-z2fqxmm2ja-uc.a.run.app/connect"); // replace with your server's URL
    socket.onopen = () => {
      console.log('WebSocket connection established.');
      socket.send(JSON.stringify(payload));
      socket.addEventListener('message', (event) => {
        const data = JSON.parse(event.data);
        console.log(data);

        if (data.type === "RESTAURANTS") {
          setRestaurants(data.payload);
        } else if (data.type === "LOCATIONS") {
          setDestinations(data.payload);
        }
      });
    };
  }, []); // empty dependency array ensures this effect only runs once

  return (
    <MapContainer center={[-33.35, -70.5]} zoom={13} scrollWheelZoom={false} style={{ height: '400px' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      {restaurants.map((restaurant) => (
        <Marker key={restaurant.id} position={[restaurant.lat, restaurant.lng]}>
          <Popup>
            <h3>{restaurant.name}</h3>
            <p>{restaurant.description}</p>
          </Popup>
        </Marker>
      ))}
      {destinations.map((destination) => (
        <Marker key={destination.id} position={[destination.lat, destination.lng]}>
          <Popup>
            <h3>{destination.name}</h3>
            <p>{destination.address}</p>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
}

export default Mapa;
