import React, { useEffect } from 'react';

function Mapa() {

    let email = "mcovarrubias@uc.cl";
    let password = "18640729";

  useEffect(() => {
    const socket = new WebSocket("wss://tarea2-2023-1-dev-z2fqxmm2ja-uc.a.run.app/connect"); // replace with your server's URL
    socket.onopen = () => {
      console.log('WebSocket connection established.');
      socket.send(JSON.stringify({ event: 'join', roomId:`Basic ${btoa(`${email}:${password}`)}` }));
    };
    socket.onmessage = (event) => {
      console.log(`Received message: ${event.data}`);
    };
    socket.onclose = (event) => {
      console.log(`WebSocket connection closed with code ${event.code}.`);
    };

    return () => {
      socket.close();
    };
  }, []); // empty dependency array ensures this effect only runs once

  return (
    <div className="App">
      <h1>WebSocket Example</h1>
    </div>
  );
}

export default Mapa;
