import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

let email = "mcovarrubias@uc.cl";
let password = "18640729";
const infopay = {
  "type": "JOIN",
  "payload": {
    "authorization": `Basic ${btoa(`${email}:${password}`)}`
  }
}
const redIcon = new L.Icon({
    iconUrl:
      'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
    shadowUrl:
      'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41],
  });

function Mapa() {
    const [map, setMap] = useState(null);
    const [restaurants, setRestaurants] = useState([]);
    const [destinations, setDestinations] = useState([]);

  useEffect(() => {
    // Initialize the UI.

    const socket = new WebSocket("wss://tarea2-2023-1-dev-z2fqxmm2ja-uc.a.run.app/connect"); // replace with your server's URL
    socket.onopen = () => {
      console.log('WebSocket connection established.');
      socket.send(JSON.stringify(infopay));
      socket.addEventListener("message", ({ data })=> {
        const event = JSON.parse(data);
        console.log(event);

        if (event.type === "RESTAURANTS") {
            event.payload.forEach(r => {
                if (!(r.id in restaurants)) {
                  restaurants[r.id] = r;
                  var marker = L.marker([r.position.lat, r.position.long]);
                  marker.bindTooltip(r.name);
                  marker.addTo(map);
                  r.marker = marker;
                }
              });
              setRestaurants({...restaurants});

        } else if (event.type === "DESTINATIONS") {
          setDestinations(event.payload);
        }
      });
    };
  }, []); // empty dependency array ensures this effect only runs once

  function handleMapLoad(map) {
    setMap(map);
  }

  return (
    <MapContainer center={[-33.35, -70.5]} zoom={13} scrollWheelZoom={true} style={{ height: '400px' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      {restaurants.map((restaurant) => (
  // Check if latitude and longitude values are defined before creating marker
  (restaurant.lat && restaurant.lng) &&
  <Marker key={restaurant.id} position={[restaurant.lat, restaurant.lng]} icon={redIcon}>
    <Popup>
      <h3>{restaurant.name}</h3>
      <p>{restaurant.description}</p>
    </Popup>
  </Marker>
))}
{destinations.map((destination) => (
  // Check if latitude and longitude values are defined before creating marker
  (destination.lat && destination.lng) &&
  <Marker key={destination.id} position={[destination.lat, destination.lng]}>
    <Popup>
      <h3>{destination.name}</h3>
      <p>{destination.address}</p>
    </Popup>
  </Marker>
))}
    </MapContainer>
  );
}

export default Mapa;
