import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

let email = "mcovarrubias@uc.cl";
let password = "18640729";
const infopay = {
  "type": "JOIN",
  "payload": {
    "authorization": `Basic ${btoa(`${email}:${password}`)}`
  }
}
const redIcon = new L.Icon({
  iconUrl:
    'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41],
});

function Mapa() {

  const [restaurants, setRestaurants] = useState([]);
  const [destinations, setDestinations] = useState([]);

  useEffect(() => {
    // Initialize the UI.
    const socket = new WebSocket("wss://tarea2-2023-1-dev-z2fqxmm2ja-uc.a.run.app/connect"); // replace with your server's URL
    socket.onopen = () => {
      console.log('WebSocket connection established.');
      socket.send(JSON.stringify(infopay));
      socket.addEventListener("message", ({ data })=> {
        const event = JSON.parse(data);
        console.log(event);
        if (event.type === "RESTAURANTS") {
          setRestaurants(event.payload);
        } else if (event.type === "DESTINATIONS") {
          setDestinations(event.payload);
        }
      });
    };
  }, []); // empty dependency array ensures this effect only runs once

  useEffect(() => {
    // Add markers for each destination
    const markers = {};
    destinations.forEach((d) => {
      markers[d.id] = L.marker([d.position.lat, d.position.lng], { icon: redIcon }).bindTooltip(d.name);
      markers[d.id].addTo(map);
    });

    // Add markers for each restaurant
    restaurants.forEach((r) => {
      markers[r.id] = L.marker([r.lat, r.lng], { icon: redIcon }).bindTooltip(r.name);
      markers[r.id].addTo(map);
    });
  }, [restaurants, destinations]);

  return (
    <MapContainer center={[-33.35, -70.5]} zoom={13} scrollWheelZoom={true} style={{ height: '400px' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
    </MapContainer>
  );
}

export default Mapa;
