import React, { useState, useEffect } from 'react';

function Mapa() {
  const [socket, setSocket] = useState(null);
  const [authenticated, setAuthenticated] = useState(false);
  const [room, setRoom] = useState('');
  const [messages, setMessages] = useState([]);
  let email = "mcovarrubias@uc.cl"; 
  let password = "18640729";

  useEffect(() => {
    const newSocket = new WebSocket("wss://tarea2-2023-1-dev-z2fqxmm2ja-uc.a.run.app/connect"); // replace with your server's URL
    setSocket(newSocket);
    return () => newSocket.close();
  }, []);

  useEffect(() => {
    if (socket) {
      socket.onmessage = (event) => {
        const message = JSON.parse(event.data);
        switch (message.type) {
          case 'authenticated':
            setAuthenticated(true);
            console.log('WebSocket connection authenticated.');
            break;
          case 'unauthenticated':
            setAuthenticated(false);
            console.log('WebSocket connection authentication failed.');
            break;
          case 'joined':
            console.log(`Joined room ${room}.`);
            break;
          case 'message':
            console.log(`Received message: ${message.data}`);
            setMessages((prevMessages) => [...prevMessages, message.data]);
            break;
          // handle other message types
          default:
            console.log(`Received message: ${event.data}`);
        }
      };
      socket.onclose = (event) => {
        console.log(`WebSocket connection closed with code ${event.code}.`);
      };
    }
  }, [socket, room]);

  const handleJoinRoom = () => {
    const token = `Basic ${btoa(`${email}:${password}`)}`; // replace with your token
    const message = { type: 'join', token, room };
    socket.send(JSON.stringify(message));
  };

  const handleSendMessage = (message) => {
    const token = `Basic ${btoa(`${email}:${password}`)}`; // replace with your token
    const newMessage = { type: 'message', token, room, data: message };
    socket.send(JSON.stringify(newMessage));
  };

  const handleLogout = () => {
    setAuthenticated(false);
    setRoom('');
    setMessages([]);
    socket.send(JSON.stringify({ type: 'leave' }));
  };

  if (!authenticated) {
    return (
      <div>
        <h1>Login</h1>
        <form onSubmit={(e) => e.preventDefault()}>
          <label>
            Room:
            <input type="text" value={room} onChange={(e) => setRoom(e.target.value)} />
          </label>
          <br />
          <button onClick={handleJoinRoom}>Join Room</button>
        </form>
      </div>
    );
  }

  return (
    <div className="App">
      <h1>WebSocket Room Example</h1>
      <p>Room: {room}</p>
      <button onClick={handleLogout}>Logout</button>
      <div>
        {messages.map((message, index) => (
          <p key={index}>{message}</p>
        ))}
      </div>
      <form onSubmit={(e) => { e.preventDefault(); handleSendMessage(e.target.elements.message.value); }}>
        <label>
          Message:
          <input type="text" name="message" />
        </label>
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default Mapa;
